    });


    function getFilesAndContent(evt) {
        $('.pr-card').hide();
        console.log(evt);
        console.log(evt.target.value);

        $.ajax({
            type: "GET",
            url: "/get-files-and-content",
            data: {
                'pull_request_number': evt.target.value
            },
            cache: false,
            success: function(data){

                let card = $('<div>').addClass('card pr-card').css('display', 'none');
                $.each(data, function(index, element) {

                    let cardHeader = $('<div>').addClass('card-header');
                    let cardBody = $('<div>').addClass('card-body');
                    let cardTitle = $('<h5>').addClass('card-title title-css').text('File : ' + element.file_name);
                    let cardText = $('<div>').addClass('card-text').html(element.file_content);
                    let reviewButton = $('<button>').addClass('btn btn-primary start-code-review').attr('onclick', `reviewCode("${element.content_url}", ${index})`).attr('type', 'button')
                        .text('Review Code');
                    let reviewResult = $('<div>').addClass('review-result-'+index).css('display', 'none');

                    card.append(cardHeader);
                    card.append(cardBody);
                    cardBody.append(cardTitle);
                    cardBody.append(cardText);
                    cardBody.append(reviewButton);
                    cardBody.append(reviewResult);


                });

                $('.github-files').html(card);

                $('.pr-card').show();
            }
        });
    }

    function reviewCode(content_url, indexElement) {

        $('.loading').show();
        $('.review-result-'+indexElement).hide();
        console.log(content_url);
        $.ajax({
            type: "GET",
            url: "/ai-code-review",
            data: {
                'file_url': content_url
            },
            cache: false,
            success: function(data){

                let card = $('<div>').addClass('card pr-card');
                $.each(data, function(index, element) {

                    let cardHeader = $('<div>').addClass('card-header');
                    let cardBody = $('<div>').addClass('card-body');
                    let cardTitle = $('<h5>').addClass('card-title review-title-css').text(index).css('text-weight', 'bold');
                    let cardText = $('<div>').addClass('card-text').html(element);

                    card.append(cardHeader);
                    card.append(cardBody);
                    cardBody.append(cardTitle);
                    cardBody.append(cardText);
                })

                $('.review-result-'+indexElement).html(card).show();
                $('.loading').hide();
            }
        });

    }

</script>

